script:
frappe.call({
      method: "frappe.client.get_list",
      args: {
        doctype: "Day Setup",
        filters: {
          date: frappe.datetime.get_today()
        },
        fields: ["day_open_time", "day_close_time"],
        limit_page_length: 1
      },
      callback: function (r) {
        let el = root_element.querySelector('#day-status');
        console.log(el)
        if (r.message && r.message.length > 0) {
          const day = r.message[0];
          if (day.day_open_time && !day.day_close_time) {
            el.classList.add("indicator-pill", "green");
            el.innerText = "Day is open for today";
          } else {
            el.classList.add("indicator-pill", "orange");
            el.innerText = "Day is not open for today";
          }
        } else {
          el.classList.add("indicator-pill", "orange");
          el.innerText = "Day is not open for today";
        }
      }
    });

frappe.call({
  method: "leaf_procurement.leaf_procurement.api.day_close_utils.get_user_audit_day_status",
  callback: function (r) {
    const el = root_element.querySelector('#day-status');
    el.innerHTML = ""; // Clear previous content

    if (!r.message || r.message.length === 0) {
      el.classList.add("indicator-pill", "orange");
      el.innerText = "No audit day found for your warehouses today";
      return;
    }

    // Split records into open and closed days
    const openDays = [];
    const closedDays = [];

    r.message.forEach(day => {
      const statusText = `${day.location_warehouse}`;
      if (day.day_open_time && !day.day_close_time) {
        openDays.push(`<div class="indicator-pill green">${statusText}</div>`);
      } else {
        closedDays.push(`<div class="indicator-pill orange">${statusText}</div>`);
      }
    });

    // Create side-by-side containers
    el.innerHTML = `
      <div style="display: flex; gap: 30px; flex-wrap: wrap;">
        <div style="flex: 1; min-width: 250px;">
          <h4>Open Days</h4>
          ${openDays.join('')}
        </div>
        <div style="flex: 1; min-width: 250px;">
          <h4>Closed/Not Open Days</h4>
          ${closedDays.join('')}
        </div>
      </div>
    `;
  }
});
