<br>

{% set ns = namespace(grouped=[], totalcount = 0, totalweight = 0) %}

<div style="text-align:center;">
    <table style="width:100%; border-collapse: collapse;">
        <tr>
            <th style="width:25%; border: 1px solid #e0e0e0;text-align:center;">Grade</th>
            <th style="width:25%; border: 1px solid #e0e0e0;text-align:center;">Total Weight</th>
            <th style="width:25%; border: 1px solid #e0e0e0;text-align:center;">Bale Count</th>
            <th style="width:25%; border: 1px solid #e0e0e0;text-align:center;">Remarks</th>
        </tr>

        {% for row in doc.bale_registration_detail %}
            {% set ns.totalcount = ns.totalcount + 1 %}
            {% set ns.totalweight = ns.totalweight + (row.weight | int ) %}
            {% set grade = (row.reclassification_grade|string)|trim %}
            {% if grade and grade not in ns.grouped %}
                {% set inner = namespace(count=0, total_weight=0) %}
                {% for r in doc.bale_registration_detail %}
                    {% if (r.reclassification_grade|string)|lower|trim == grade|lower %}
                        {% set inner.count = inner.count + 1 %}
                        {% set inner.total_weight = inner.total_weight + (r.weight or 0) | float %}
                    {% endif %}
                {% endfor %}

                <tr>
                    <td style="border: 1px solid #e0e0e0;">{{ grade }}</td>
                    <td style="border: 1px solid #e0e0e0;">{{ '%.2f' % inner.total_weight }}</td>
                    <td style="border: 1px solid #e0e0e0;">{{ inner.count }}</td>
                    <td style="border: 1px solid #e0e0e0;"></td>
                </tr>

                {% set ns.grouped = ns.grouped + [grade] %}
            {% endif %}
        {% endfor %}
        <tr>
            <td style="border: 1px solid #e0e0e0;">
                <b>Total</b>
            </td>
            <td style="border: 1px solid #e0e0e0;">
                <b>{{ '%.2f' % ns.totalweight}}</b>
            </td>
            <td style="border: 1px solid #e0e0e0;">
                <b>{{ns.totalcount}}</b>
            </td>
            <td style="border: 1px solid #e0e0e0;"></td>
        </tr>
    </table>
</div>
"}]
