[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">				
<h2><div>Goods Transfer Note</div><small class="sub-heading">{{ _(doc.name) }}</small>				</h2></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "company", "print_hide": 0, "label": "Company"}, {"fieldname": "date", "print_hide": 0, "label": "Date"}, {"fieldname": "vehicle_number", "print_hide": 0, "label": "Vehicle Number"}, {"fieldtype": "Column Break"}, {"fieldname": "location_warehouse", "print_hide": 0, "label": "Dispatch Location"}, {"fieldname": "transit_location", "print_hide": 0, "label": "Transit Location"}, {"fieldname": "driver_name", "print_hide": 0, "label": "Driver Name"}, {"fieldname": "driver_mobile", "print_hide": 0, "label": "Driver Mobile"}, {"fieldtype": "Column Break"}, {"fieldname": "default_item", "print_hide": 0, "label": "Item"}, {"fieldname": "receiving_location", "print_hide": 0, "label": "Receiving Location"}, {"fieldname": "tsa_number", "print_hide": 0, "label": "TSA Number"}, {"fieldname": "receiving_warehouse_mobile", "print_hide": 0, "label": "Receiving Warehouse Mobile"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "bale_registration_detail", "print_hide": 0, "visible_columns": [{"fieldname": "bale_barcode", "print_width": "", "print_hide": 0}, {"fieldname": "weight", "print_width": "", "print_hide": 0}, {"fieldname": "rate", "print_width": "", "print_hide": 0}, {"fieldname": "item_grade", "print_width": "", "print_hide": 0}, {"fieldname": "reclassification_grade", "print_width": "", "print_hide": 0}]}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="font-size:15pt;text-align:center;">
    <b>GTN Summary</b>
</div>
{% set grades = namespace(data={}, total_weight=0.0, total_count=0) %}

{% for row in doc.bale_registration_detail %}
    {% set grade = (row.reclassification_grade|string)|trim %}
    {% set weight = (row.weight or 0) | float %}
    {% if grade %}
        {% if not grades.data.get(grade) %}
            {% set _ = grades.data.update({ grade: {'weight': 0.0, 'count': 0} }) %}
        {% endif %}
        {% set _ = grades.data[grade].update({
            'weight': grades.data[grade].weight + weight,
            'count': grades.data[grade].count + 1
        }) %}
        {% set grades.total_weight = grades.total_weight + weight %}
        {% set grades.total_count = grades.total_count + 1 %}
    {% endif %}
{% endfor %}

<div style="text-align:center;">
    <table class="table-bordered" style="width:100%">
        <tr>
            <th style="width:25%; text-align:center;">Grade</th>
            <th style="width:25%; text-align:center;">Total Weight</th>
            <th style="width:25%; text-align:center;">Bale Count</th>
            <th style="width:25%; text-align:center;">Remarks</th>
        </tr>
        {% for grade, data in grades.data.items() %}
        <tr>
            <td>{{ grade }}</td>
            <td style="text-align:right">{{ "{:,.2f}".format(data.weight) }}</td>
            <td style="text-align:right">{{ data.count }}</td>
            <td></td>
        </tr>
        {% endfor %}

        <tr>
            <td><b>Total</b></td>
            <td style="text-align:right"><b>{{ "{:,.2f}".format(grades.total_weight) }}</b></td>
            <td style="text-align:right"><b>{{ grades.total_count }}</b></td>
            <td></td>
        </tr>

    </table>
</div>

"}]
